mapper.update:
UPDATE product
name = #{name}, category_id = #{category_id}, price = #{price}, discount_price = #{discount_price}, freight_price = #{freight_price}, payment_type = #{payment_type}, quantity = #{quantity}, detail = #{detail}, default_img = #{default_img}, status = #{status}, hot = #{hot}
WHERE id = #{id}

mapper.select:
SELECT 
	p.*, 
	(SELECT AVG(pc.score) FROM product_comment pc WHERE p.id = pc.pid ) AS score,
	(SELECT COUNT(1) FROM product_comment pc WHERE p.id = pc.pid ) AS common_num,
	(SELECT SUM(oi.quantity) FROM orders_item oi WHERE p.id = oi.pid) AS saled_num
FROM product p 

	p.invisible = 0
	AND p.name LIKE '%${param.name}%'
	AND p.status = #{param.status}
	AND p.category_id = #{param.category_id}
	AND p.hot = #{param.hot}


	LIMIT #{param.page},#{param.rows}

SELECT COUNT(1) FROM product p 

	p.invisible = 0
	AND p.name LIKE '%${param.name}%'
	AND p.status = #{param.status}
	AND p.category_id = #{param.category_id}
	AND p.hot = #{param.hot}
